import classes from './SocialWidget.module.css'
import twitterLogoText from '../static/logos/twitterLogoText.png'
import instagramLogoText from '../static/logos/instagramLogoText.png'
import facebookLogoText from '../static/logos/facebookLogoText.jpeg'
// EDIT: also add logo icon!
const SocialWidget = ({mediaType}) => {
    let mediaConfig = {}
    switch (mediaType) {
        case "TWITTER":
            mediaConfig.bgStyle = classes.twitterBG
            mediaConfig.logoText = "static/twitterLogoText.png"
            break;
        case "INSTAGRAM":
            mediaConfig.bgStyle = classes.instagramBG
            mediaConfig.logoText = "static/instagramLogoText.png"
            break;
        case "FACEBOOK":
            mediaConfig.bgStyle = classes.facebookBG
            mediaConfig.logoText = "static/facebookLogoText.jpeg"
            break;
        default:
            mediaConfig.bgStyle = classes.defaultBG;
    }
    return (
        <div className={classes.container} id={mediaConfig.bgStyle}>SocialWidget
            <img src="static/facebookLogoText.jpeg" alt="meow"/>
        </div>
    )
}

export default SocialWidget
